mapper.select:
SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where REGDATE >= TO_CHAR(SYSDATE-7,'YYYYMMDD' )) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID ORDER BY a.LIKEHIT desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD WHERE BGNO = 20 or BGNO = 21 or BGNO = 22 or BGNO = 23 ) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID where QUESTIONCHECK = 0 ORDER BY a.BNO desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where REGDATE >= TO_CHAR(SYSDATE-2,'YYYYMMDD' )) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID where bgno = 1 ORDER BY a.LIKEHIT desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where REGDATE >= TO_CHAR(SYSDATE-2,'YYYYMMDD' )) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID where bgno = 3 ORDER BY a.LIKEHIT desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where REGDATE >= TO_CHAR(SYSDATE-2,'YYYYMMDD' )) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID where bgno = 4 ORDER BY a.LIKEHIT desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where REGDATE >= TO_CHAR(SYSDATE-2,'YYYYMMDD' )) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID where bgno = 6 ORDER BY a.LIKEHIT desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM (select * from MP_BOARD where likehit >= 20) a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id=#{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID ORDER BY a.bno desc

SELECT a.BNO, a.TITLE, a.CONTENT, a.WRITER, a.REGDATE, a.HIT, a.REPLYHIT, a.BGNO, a.LIKEHIT, a.HATEHIT, a.ID, a.DEVHIT, a.QUESTIONCHECK, a.HELPPOINT, b.BNO, b.MEMBER_ID, c.MEMBER_ID, c.MEMBER_DEVPOINT, c.MEMBER_POINT, c.MEMBER_IMG, c.MEMBER_NAME FROM ( SELECT BNO, TITLE, CONTENT, WRITER, REGDATE, HIT , REPLYHIT, BGNO, LIKEHIT, HATEHIT, ID, DEVHIT, QUESTIONCHECK, HELPPOINT, ROW_NUMBER() OVER(ORDER BY BNO DESC) AS RNUM FROM MP_BOARD WHERE likehit >= 20
)a left outer JOIN MP_BOARD_CHECK b on a.bno = b.bno
AND b.member_id = #{memberId}
left outer Join MP_MEMBER c on a.ID = c.member_ID WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} order by a.bno desc
